export function encodeToString(t){let e="";for(let n=0;n<t.length;n++){let o=t[n];if(o<=127){e+=String.fromCodePoint(o);continue}if(n+1<t.length&&o>=194&&o<=223){let r=t[n+1];if(r>=128&&r<=191){e+=String.fromCodePoint((31&o)<<6|63&r),n++;continue}}e+=String.fromCodePoint(56320|o)}return e}export function encodeFromString(t){let e=Buffer.alloc(2*t.length),n=0;for(let o=0;o<t.length;o++){let r=t.codePointAt(o);r<=127?e[n++]=r:r>=56320&&r<=56575?e[n++]=255&r:(e[n++]=192|r>>6,e[n++]=128|63&r)}return e.subarray(0,n)}